INSERT INTO `explicit` (`id_explicit`, `id_pengguna`, `judul_explicit`, `keterangan`, `userfile`, `tgl_post`, `validasi_explicit`, `bulan`, `tahun`) VALUES
(1, 7, 'Merawat Kabel Fiber Optik (Arsip Puskom IAIN Bengkulu)', '<p>Agar perangkat maupun jaringan kabel fiber optic dapat digunakan kapan saja, dan tahan lama maka perlu diadakan pemeliharaan rutin di tiap gedung fakultas yang memakai kabel fiber optik untuk jaringan internet.</p>\r\n', 'Merawat Kabel Fiber Optik (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 19:47:05', 1, '01', 2020),
(2, 8, 'Gangguan Domain dan Website tidak bisa diakses (Arsip Puskom IAIN Bengkulu)', '<p>Berikut merupakan Dokumen yang menjelaskan mengenai penyebab akibat dari gangguan domain serta website yang tidak bisa diakses. Mengenai hal ini pernah terjadi pada website IAIN Bengkulu<br />\r\n&nbsp;</p>\r\n', 'Gangguan Domain dan Website tidak bisa diakes (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 19:48:14', 1, '01', 2020),
(3, 8, 'Langkah-Langkah Konfigurasi Access Point (Arsip Puskom IAIN Bengkulu)', '<p>Di dalam Dokumen ini terdapat 2 metode Langkah-Langkah untuk melakukan Konfigurasi Access Point untuk semua Perangkat Access Point di IAIN Bengkulu.<br />\r\n&nbsp;</p>\r\n', 'Langkah-Langkah Konfigurasi Access Point (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 19:48:44', 1, '01', 2020),
(4, 7, 'Instalasi Kabel Fiber Optik (Arsip Puskom IAIN Bengkulu)', '<p>Berikut merupakan tahap Instalasi Kabel Fiber Optik yang singkat dan jelas sebagai pendukung referensi jika mau melakukan Instalasi Kabel Fiber Optik kembali</p>\r\n', 'Instalasi Kabel Fiber Optik (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 20:34:08', 1, '01', 2020),
(5, 8, 'Langkah-Langkah Konfigurasi Jaringan Internet (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen yang berisi panduan lengkap mengenai prosedur dari langkah-langkah konfigurasi Jaringan Internet yang bisa digunakan jika terjadi gangguan internet kembali</p>\r\n', 'Langkah-Langkah Konfigurasi Jaringan Internet (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 20:40:29', 1, '01', 2020),
(6, 7, 'Cara Mengatasi Masalah Perangkat pada Gangguan Jaringan (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen ini berisi mengenai beberapa cara mengatasi perangkat pada gangguan jaringan yang menyebabkan perangkat menjadi rusak dan gangguan jaringan internet</p>\r\n', 'Cara Mengatasi Masalah Perangkat pada Gangguan Jaringan.pdf', '2020-01-03 21:24:12', 1, '01', 2020),
(7, 7, 'Prosedur umum mengenai Jaringan LAN (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen ini merupakan Prosedur mengenai Jaringan LAN secara umum sebagai referensi dalam mengenali jaringan LAN</p>\r\n', 'Prosedur umum mengenai Jaringan LAN (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 21:43:41', 1, '01', 2020),
(8, 8, 'Cara mengatasi berbagai macam masalah trouble shooting pada kabel LAN (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen ini berisi tentang prosedur mengenai&nbsp;masalah trouble shooting pada kabel LAN di Puskom IAIN Bengkulu</p>\r\n', 'Cara mengatasi berbagai macam masalah trouble shooting pada kabel LAN (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 22:07:17', 1, '01', 2020),
(9, 7, 'Jenis-Jenis serta tipe Kabel UTP pada Jaringan Komputer (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen ini berisi penjelasan mengenai jenis-jenis serta tipe dari kabel UTP sebagai perangkat pendukung jaringan komputer</p>\r\n', 'Jenis-Jenis serta tipe Kabel UTP pada Jaringan Komputer.pdf', '2020-01-03 22:12:19', 1, '01', 2020),
(10, 8, 'Cara melakukan pemasangan pada kabel UTP (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen ini berisi prosedur dalam melakukan pemasangan pada Kabel UTP sebagai referensi untuk membangun jaringan internet&nbsp;</p>\r\n', 'Cara melakukan pemasangan pada kabel UTP (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 22:18:12', 1, '01', 2020),
(11, 8, 'Cara memperbaiki hub switch yang rusak (Arsip Puskom IAIN Bengkulu)', '<p>Berikut merupakan prosedur langkah-langkah&nbsp;untuk memperbaiki hub switch yang rusak</p>\r\n', 'Cara memperbaiki hub switch yang rusak (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-03 22:22:00', 1, '01', 2020),
(12, 7, 'Cara memperbaiki Jaringan pada Perangkat Tplink (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen ini berisi tentang berbagai cara dalam memperbaiki jaringan beberapa perangkat tplink yang digunakan di Puskom IAIN Bengkulu</p>\r\n', 'Cara memperbaiki jaringan pada perangkat tpling (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-04 20:20:58', 1, '01', 2020),
(13, 7, 'Memperbaiki Kabel UTP (Arsip Puskom IAIN Bengkulu)', '<p>Dokumen ini berisi tentang berbagai penyebab kabel utp rusak serta cara memperbaiki berbagai kerusakan kabel utp yang menjadi penyebab kabel utp rusak</p>\r\n', 'Cara memperbaiki kabel UTP (Arsip Puskom IAIN Bengkulu).pdf', '2020-01-04 20:22:36', 0, '01', 2020);

